import {Link} from 'react-router-dom';
import MountainBackground from './Background';
const Credits = (props) => {


    let viewCreditsDesc = () =>{
        const {credits} = props
        return credits.map((credit) => {
            return <li className="creditListItem" key={credit.id}>{credit.description}</li>
        });
    }
    let viewCreditsAmount = () =>{
        const {credits} = props
        return credits.map((credit) => {
            return <li className="creditListItem" key={credit.id}>{credit.amount}</li>
        });
    }
    let viewCreditsDate = () =>{
        const {credits} = props
        return credits.map((credit) => {
            let date = credit.date.slice(0,10);
            return <li className="creditListItem" key={credit.id}>{date}</li>
        });
    }
        return ( 
            <div className="CreditsContainer">
                <MountainBackground />
                <h1>Credits</h1>
                <br/>
                <div className="ListLabels"> 
                    <h2>Description</h2>
                    <h2>Amount</h2>
                    <h2>Date</h2>
                </div>
                <div className="displayList">
                    <div className="column">{viewCreditsDesc()}</div>
                    <div className="column">{viewCreditsAmount()}</div>
                    <div className="column">{viewCreditsDate()}</div>
                </div>

                <form onSubmit={props.addCredit}>
                    <input type="text" name="description" placeholder='Description'/>
                    <input type="number" name="amount" placeholder='Amount'/>
                    <button type="submit">Add Credit</button>
                </form>
                <Link to="/">Return to Home</Link>            
            </div>
        );
}

export default Credits;
