import React, {Component} from 'react';

class AccountBalance extends Component {
    render() {
        return (
            <div
                style={{
                color: this.props.accountBalance < 0 ? 'red' : 'green',
                fontWeight: 'bold'
  }}
>
  Balance: {this.props.accountBalance < 0 
    ? `-$${Math.abs(this.props.accountBalance)}` 
    : `$${this.props.accountBalance}`}
</div>
        );
    }
}

export default AccountBalance;
